body.script:
    //
    (function() {
        // Í≥ÑÏ∏µÏ†Å Ïπ¥ÌÖåÍ≥†Î¶¨ Íµ¨Ï°∞ ÏÉùÏÑ±
        function createHierarchicalCategories() {
            const categoriesContainer = document.getElementById('hierarchical-categories');
            if (!categoriesContainer) return;

            // JSON feedÏóêÏÑú Ìè¨Ïä§Ìä∏ Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
            fetch('/feeds/posts/default?alt=json&max-results=9999')
                .then(response => response.json())
                .then(data => {
                    const posts = data.feed.entry || [];
                    const categoryHierarchy = {};

                    // Í∞Å Ìè¨Ïä§Ìä∏Ïùò ÎùºÎ≤® Î∂ÑÏÑù
                    posts.forEach(post => {
                        if (post.category && post.category.length > 0) {
                            // Î™®Îì† ÎùºÎ≤®ÏùÑ ÏàúÌöåÌïòÎ©¥ÏÑú ">" Íµ¨Î∂ÑÏûêÎ°ú Í≥ÑÏ∏µ Íµ¨Ï°∞ ÌååÏã±
                            post.category.forEach(category => {
                                const label = category.term;
                                if (typeof label !== 'string') {
                                    return;
                                }

                                if (label.indexOf('>') === -1) {
                                    return;
                                }

                                const parts = label.split('>').map(part => part.trim()).filter(Boolean);
                                if (parts.length < 2) {
                                    return;
                                }

                                const rootCategory = parts[0];
                                const subKey = parts.slice(1).join(' > ');

                                if (!categoryHierarchy[rootCategory]) {
                                    categoryHierarchy[rootCategory] = {
                                        count: 0,
                                        subcategories: {}
                                    };
                                }

                                categoryHierarchy[rootCategory].count++;

                                if (!categoryHierarchy[rootCategory].subcategories[subKey]) {
                                    categoryHierarchy[rootCategory].subcategories[subKey] = {
                                        count: 0,
                                        fullLabel: label
                                    };
                                }
                                categoryHierarchy[rootCategory].subcategories[subKey].count++;
                            });
                        }
                    });

                    // Í≥ÑÏ∏µ Íµ¨Ï°∞ HTML ÏÉùÏÑ±
                    const categoryList = categoriesContainer.querySelector('.category-list');
                    if (categoryList && Object.keys(categoryHierarchy).length > 0) {
                        categoryList.innerHTML = '';

                        Object.keys(categoryHierarchy).sort().forEach(rootCat => {
                            const rootData = categoryHierarchy[rootCat];
                            
                            // Î£®Ìä∏ Ïπ¥ÌÖåÍ≥†Î¶¨
                            const rootLi = document.createElement('li');
                            rootLi.className = 'category-root';
                            
                            const rootLink = document.createElement('a');
                            rootLink.href = '/search/label/' + encodeURIComponent(rootCat);
                            
                            const toggleSpan = document.createElement('span');
                            toggleSpan.className = 'category-toggle';
                            toggleSpan.innerHTML = '‚ñº';
                            
                            const nameSpan = document.createElement('span');
                            nameSpan.textContent = rootCat;
                            
                            const countSpan = document.createElement('span');
                            countSpan.className = 'category-count';
                            countSpan.textContent = '(' + rootData.count + ')';
                            
                            rootLink.appendChild(toggleSpan);
                            rootLink.appendChild(nameSpan);
                            rootLink.appendChild(countSpan);
                            rootLi.appendChild(rootLink);
                            categoryList.appendChild(rootLi);

                            // ÏÑúÎ∏å Ïπ¥ÌÖåÍ≥†Î¶¨
                            const subcategories = rootData.subcategories;
                            if (Object.keys(subcategories).length > 0) {
                                const subUl = document.createElement('ul');
                                subUl.className = 'category-subcategories';
                                subUl.style.maxHeight = '500px'; // Ï¥àÍ∏∞ ÏÉÅÌÉúÎäî ÌéºÏ≥êÏßÑ ÏÉÅÌÉú

                                Object.keys(subcategories).sort().forEach(subCat => {
                                    const subData = subcategories[subCat];
                                    const subLi = document.createElement('li');
                                    subLi.className = 'category-sub';
                                    
                                    const subLink = document.createElement('a');
                                    // Ï†ÑÏ≤¥ ÎùºÎ≤®Î°ú Í≤ÄÏÉâ (Ïòà: "ÏùºÎ≥∏>2024 ÌôãÏπ¥Ïù¥ÎèÑ")
                                    subLink.href = '/search/label/' + encodeURIComponent(subData.fullLabel || subCat);
                                    
                                    const subNameSpan = document.createElement('span');
                                    subNameSpan.textContent = subCat;
                                    
                                    const subCountSpan = document.createElement('span');
                                    subCountSpan.className = 'category-count';
                                    subCountSpan.textContent = '(' + subData.count + ')';
                                    
                                    subLink.appendChild(subNameSpan);
                                    subLink.appendChild(subCountSpan);
                                    subLi.appendChild(subLink);
                                    subUl.appendChild(subLi);
                                });

                                categoryList.appendChild(subUl);

                                // ÌÜ†Í∏Ä Í∏∞Îä• Ï∂îÍ∞Ä
                                rootLink.addEventListener('click', function(e) {
                                    if (e.target.className === 'category-toggle' || e.target.parentElement.className === 'category-toggle') {
                                        e.preventDefault();
                                        rootLi.classList.toggle('collapsed');
                                        
                                        if (rootLi.classList.contains('collapsed')) {
                                            subUl.style.maxHeight = '0';
                                        } else {
                                            subUl.style.maxHeight = '500px';
                                        }
                                    }
                                });
                            }
                        });
                    }
                })
                .catch(error => {
                    console.log('Ïπ¥ÌÖåÍ≥†Î¶¨ Íµ¨Ï°∞ ÏÉùÏÑ± Ï§ë Ïò§Î•ò:', error);
                });
        }

        // DOM Î°úÎî© ÏôÑÎ£å ÌõÑ Ïã§Ìñâ
        if (document.readyState === 'loading') {
            document.addEventListener('DOMContentLoaded', createHierarchicalCategories);
        } else {
            createHierarchicalCategories();
        }

        // Î∂ÄÎìúÎü¨Ïö¥ Ïä§ÌÅ¨Î°§ Í∏∞Îä•
        function initSmoothScroll() {
            document.querySelectorAll('a.nav-link[href^="#"]').forEach(anchor => {
                anchor.addEventListener('click', function(e) {
                    const href = this.getAttribute('href');
                    if (href && href.startsWith('#')) {
                        const targetId = href.substring(1);
                        const targetElement = document.getElementById(targetId);
                        if (targetElement) {
                            e.preventDefault();
                            targetElement.scrollIntoView({
                                behavior: 'smooth',
                                block: 'start'
                            });
                        }
                    }
                });
            });
        }

        // DOM Î°úÎî© ÏôÑÎ£å ÌõÑ Î∂ÄÎìúÎü¨Ïö¥ Ïä§ÌÅ¨Î°§ Ï¥àÍ∏∞Ìôî
        if (document.readyState === 'loading') {
            document.addEventListener('DOMContentLoaded', initSmoothScroll);
        } else {
            initSmoothScroll();
        }
    })();
    //
